mapper.select:







SELECT b.user_no, b.user_id, b.nick_name
FROM
(
SELECT target_no FROM nuri_friend
WHERE user_no = #{userNo}
AND relationship = #{relationship}
) A
, nuri_user B
WHERE A.target_no = B.user_no

SELECT b.user_no, b.user_id, b.nick_name
