mapper.update:
UPDATE nuri_friend
SET
relationship = #{relationship}
WHERE user_no = #{userNo} AND target_no = #{targetNo}

mapper.select:
SELECT relationship
FROM nuri_friend
WHERE user_no = #{userNo} AND target_no = #{targetNo}
