mapper.update:
UPDATE nuri_reply
SET content = #{content}
WHERE reply_no = #{replyNo}

mapper.select:

SELECT channel_no, board_no, reply_no, content, nick_name, reply_recommend, reg_date
FROM
(
SELECT
  rownum rn, channel_no, board_no, reply_no, content, nick_name, reply_recommend, reg_date
FROM nuri_reply
WHERE board_no = #{bno}
    AND rownum <= #{cri.page} * #{cri.amount}
)
WHERE rn > (#{cri.page} -1) * #{cri.amount}

SELECT COUNT(*)
FROM nuri_reply
WHERE board_no = #{boardNo}
